mapper.select:
select * from( select rownum as rn, o_no, o_sortation, o_ordernumber, o_orderdate, o_name,O_PRODUCT, o_phone, o_ordername, o_price, o_paymethod, o_deliverymethod, o_step,O_STORESHOP,O_TIREINSTALLDATE,O_EMAIL, O_CARYEAR,O_CARBRAND,O_CARNAME,O_CARNUMBER,O_REQUEST from( select * from product_order where o_name like '%'||#{nameInput}||'%' and o_phone like '%'||#{phoneInput}||'%' and o_ordernumber like '%'||#{orderNumberInput}||'%'
and o_orderdate BETWEEN TO_DATE(#{dateSearch1}) AND TO_DATE(#{dateSearch2}) + 1
order by o_no)) where rn >= #{start} and rn <= #{end}
